<script>
       document.addEventListener('scroll', function () {
    var sections = document.querySelectorAll('section');

    sections.forEach(function (section) {
        var top = section.getBoundingClientRect().top;

        if (top < window.innerHeight / 2) {
            section.classList.add('active');

            // Mengambil id dari bagian yang sedang aktif
            var currentSectionId = section.getAttribute('id');

            // Menghapus kelas 'active' dari semua tombol navbar
            document.querySelectorAll('.nav-item').forEach(function (button) {
                button.classList.remove('active');
            });

            // Menandai tombol navbar yang sesuai dengan bagian yang sedang aktif
            var activeButton = document.querySelector('.nav-item[href="#' + currentSectionId + '"]');
            if (activeButton) {
                activeButton.classList.add('active');
            }
        } else {
            section.classList.remove('active');
        }
    });
});

document.querySelectorAll('.nav-item').forEach(item => {
    item.addEventListener('click', function (event) {
        event.preventDefault(); // Mencegah aksi default dari link

        var targetId = this.getAttribute('href').substring(1);
        var targetElement = document.getElementById(targetId);

        targetElement.scrollIntoView({
            behavior: 'smooth',
            block: 'start',
            inline: 'start',
        });

        document.querySelectorAll('.nav-item').forEach(button => {
            button.classList.remove('active');
        });

        this.classList.add('active');
    });
});

    </script>